FROM <http://rdfdata.eionet.europa.eu/eunis/speciesconservation.rdf.gz> 
FROM <http://rdfdata.eionet.europa.eu/eea/biogeographic-regions.rdf> 
FROM <http://rdfdata.eionet.europa.eu/eea/countries.rdf> 
